<template>
  <div class="c-DrawingBoardTest">
    <button @click="handleRevoke">撤销</button>
    <button @click="handleClear">clear</button>
    <button @click="handleDownload">下载</button>
    <button @click="handleGetDataUrl">getDataUrl</button>
    <button @click="handleRotate">旋转</button>

    <hr>
    <input
      type="color"
      @change="handleSetPenColor">

    <input
      type="range"
      @change="handleSetPenWidth">

    <hr>
    <div id="board"/>
  </div>
</template>

<script>
/**
 * * DrawingBoardTest
 */

import DrawingBoard from 'Plugins/drawing-board'

export default {
  name: 'DrawingBoardTest',
  components: {},
  mixins: [],
  props: {},
  data() {
    return {
      stack: [],
      count: 0
    }
  },
  computed: {},
  watch: {},
  beforeCreate() {},
  created() {},
  mounted() {
    this.drawingBoard = new DrawingBoard('#board', {
      bgImgURL: 'data:image/png;base64,',
      className: 'c-MyCanvas',
      size: [300, 400],
      bgImgRotate: 90,
      onRevokeStackChange(stack) {
        console.log('onRevokeStackChange回调', stack)
      },
      onPaintEnd(paintCount) {
        console.log('onPaintEnd回调', paintCount)
      }
    })

    console.log(this.drawingBoard)

    window.board = this.drawingBoard
  },
  methods: {
    handleRevoke() {
      this.drawingBoard.revoke()
    },
    handleClear() {
      this.drawingBoard.clear()
    },
    handleDownload() {
      this.drawingBoard.download()
    },
    handleGetDataUrl() {
      console.log(this.drawingBoard.getDataUrl())
    },
    handleSetPenColor(e) {
      const color = e.target.value

      this.drawingBoard.setPenStyle({ color })
    },
    handleSetPenWidth(e) {
      const width = parseInt(e.target.value)

      this.drawingBoard.setPenStyle({ width })
    },
    handleRotate() {
      this.drawingBoard.rotate()
    }
  }
}
</script>

<style lang="scss" scoped>
.c-DrawingBoardTest {
  /deep/ .c-MyCanvas {
    border: 1px solid blue;
  }
}
</style>
